package com.lzq.demo.utils;import com.github.javafaker.Faker;import org.modelmapper.ModelMapper;/** * @author: lzq * @since: 2023-04-24 **/public class TestUtil {    private static final ModelMapper MODEL_MAPPER = new ModelMapper();    private static final Faker faker = new Faker();    public static <T> T generate(Class<T> clazz) {        T entity = MODEL_MAPPER.map(faker, clazz);        MODEL_MAPPER.validate();        return entity;    }}